import React from "react";
import Today from "./Today"
import Forecast from "./Forecast";
import "./App.css";

class App extends React.Component {
  constructor() {
    super();

    this.state = {weather: undefined}
  }

  getWeather = async () => {

    var weather = require('openweather-apis');
    weather.setAPPID('5c50080aa56953849755237b005e9f6f');
    weather.setCity('Castelfidardo');
    weather.setUnits('metric');

    const response = await weather.getAllWeather(function(error, JSONObj) {
      console.log('caca', JSONObj);
    });

    console.log(response);

    this.setState({
      temperature: response.main.temp,
      city: response.name,
      country: response.sys.country,
      humidity: response.main.humidity,
      description: response.weather[0].description,
      error: ""
    })

  }  

  render() {
    return (
      <div className="box">
      	<Today />
      	<Forecast temperature={getWeather}/>

      </div>
    );
  }
}
export default App;
