import React from "react";
import Today from "./Today"
import Day from "./Day";
import "./App.css";

const Api_Key = '5c50080aa56953849755237b005e9f6f';

class App extends React.Component {
state = {

    temperature: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    error: undefined,
    forecast: {
      data: undefined
    }
  }

  //getWeather is a method we'll use to make the api call
  getWeather = async () => {

    const city = 'Castelfidardo'
    const country = 'Italy'; 

    // Current weather api call
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&units=metric&appid=${Api_Key}`);
    const response = await api_call.json();

    // Forecast weather api call
    const forecast_api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&units=metric&cnt=6&appid=${Api_Key}`);
    const forecast_response = await forecast_api_call.json();

    console.log(forecast_response);

    function getDayName(dateString) {
      var days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
      var d = new Date(dateString);
      var dayName = days[d.getDay()];
      return dayName;
    }

    if (city && country){
      this.setState({
        temperature: response.main.temp,
        city: response.name,
        country: response.sys.country,
        description: response.weather[0].main,
        icon: response.weather[0].icon,
        forecast: {
          day1: forecast_response.list[1],
          day2: forecast_response.list[2],
          day3: forecast_response.list[3],
          day4: forecast_response.list[4],
          day5: forecast_response.list[5],
        },
        error: ""
      })
      console.log(state);
    } else {
      this.setState({
        error: "Please input search values..."
      })
    }
  }

  componentDidMount = () => {
    this.getWeather();
  }

  render() {
    return (
      <div className="box">
      	<Today 
          temperature={this.state.temperature} 
          city={this.state.city} 
          country={this.state.country} 
          description={this.state.description} 
          icon={this.state.icon} 
        />
      	<div className='forecast'>
          <li>
            <Day dayname={this.getDayName(this.state.forecast.day1)}
               />
          </li>
        </div>

      </div>
    );
  }
}
export default App;
