import React from "react";
import Day from "./Day";
import PropTypes from 'prop-types';

class Forecast extends React.Component {

  render() {
  	var items = this.props.days;

  	var allDays = Object.keys(this.props.days).map(function(key) {
			return <li>
				<Day 
					dayname="Tue" 
					tmpMax={items[key]} 
					tmpMin={items[key]} 
					iconName={items[key]} 
				/>
			</li>;
		});
  	return (
			<ul className="forecast">
				{ allDays	}
			</ul>
    );
  }
}

Forecast.propTypes = {
  days: PropTypes.array
};

export default Forecast;
