import React from "react";
import Day from "./Day";
import PropTypes from 'prop-types';

class Forecast extends React.Component {
	constructor (props) {
		super(props);
		props = {days: []};
	}

	allDays = (props) => {
		const content = props.days.map((day) =>
			<li>
				<Day dayname={day.dt_text}
					 />
			</li>
		)

		return (
			<ul className="forecast">
				{content}  			
			</ul>
			)	
	}
render() {return allDays};

}

Forecast.propTypes = {
  days: PropTypes.array
};

export default Forecast;
