import React from "react";
import Today from "./Today"
import Forecast from "./Forecast";
import "./App.css";

class App extends React.Component {
  constructor() {
    super();

    this.state = {weather: undefined}
  }
  componentDidMount() {
    this.getWeather();
  }

  getWeather() {
    var weather = require('openweather-apis');
    weather.setAPPID('5c50080aa56953849755237b005e9f6f');
    weather.setCity('Castelfidardo');
    weather.setUnits('metric');

    this.state = {weather: weather.getAllWeather()};
  }


  render() {
    return (
      <div className="box">
      	<Today />
      	<Forecast weather={this.state.weather}/>

      </div>
    );
  }
}
export default App;
